#!/usr/bin/env python# -*- coding: utf-8 -*-import sys#sys.path.append("..")import game, othellofrom Joueurs import joueur_humain, joueur_minimax, joueur_aleatoire, joueur_horizon_1game.game=othello#sys.path.append("./Joueurs")game.joueur1= joueur_aleatoiregame.joueur2= joueur_minimaxjeu = game.game.initialiseJeu()while not game.game.finJeu(jeu):    copie_jeu = game.game.getCopieJeu(jeu)    game.game.affiche(copie_jeu)    # time.sleep(2)    num_joueur_courant = game.game.getJoueur(copie_jeu)    if num_joueur_courant == 1:        coup = game.joueur1.saisieCoup(copie_jeu)    else:        coup = game.joueur2.saisieCoup(copie_jeu)    if game.game.coupValide(copie_jeu, coup):        game.game.joueCoup(copie_jeu, coup)        plateau, joueur_courant, coups_possibles, coups_joues, (_,_) = copie_jeu        game.game.changeJoueur(copie_jeu)        jeu = plateau, game.game.joueurSuivant, coups_possibles, coups_joues + [coup], (        game.game.score1, game.game.score2)        coups_possibles = game.game.getCoupsValides(jeu)        if len(coups_possibles) == 0: # Joueur bloque ou vers la fin de jeu            game.game.changeJoueur(jeu)            jeu = plateau, game.game.joueurSuivant, _, _, (_, _)            coups_possibles = game.game.getCoupsValides(jeu)        jeu = plateau, game.game.joueurSuivant, coups_possibles, coups_joues + [coup], (        game.game.score1, game.game.score2)game.game.affiche(jeu)gagnant = game.game.getGagnant(jeu)score1 = game.game.getScores(jeu)[0]score2 = game.game.getScores(jeu)[1]if gagnant == 0:    print("Match nul : {} : {}".format(score1, score2))elif gagnant == 1:    print("Victoire du Joueur 1 : \n{} a {}".format(score1, score2))else :    print("Victoire du Joueur 2 : \n{} : {}".format(score2, score1))